    firebim:hasMember firebim:Member_3_5_2_2_1_1 .

firebim:Member_3_5_2_2_1_1 a firebim:Member ;
    firebim:hasOriginalText """De dubbelwandige gevels zonder compartimentering moeten in overeenstemming zijn met een
van de twee hierna opgenomen mogelijkheden."""@nl .

firebim:Section_3_5_2_2_1 a firebim:Section ;
