    firebim:hasOriginalText "Dubbelwandige gevel waarvan de binnenwand brandwerend is."@nl ;
    firebim:hasArticle firebim:Article_3_5_2_2_1_1 .

firebim:Article_3_5_2_2_1_1 a firebim:Article ;
    firebim:hasID "A3.5.2.2.1.1" ;
    firebim:hasMember firebim:Member_3_5_2_2_1_1_1, firebim:Member_3_5_2_2_1_1_2 .

firebim:Member_3_5_2_2_1_1_1 a firebim:Member ;
    firebim:hasOriginalText """De buitenwand van de dubbelwandige gevel bestaat tussen de verdiepingen voor minstens 50%
uit bouwelementen zonder specifieke brandweerstand."""@nl .

firebim:Member_3_5_2_2_1_1_2 a firebim:Member ;
    firebim:hasOriginalText "De binnenwand heeft:"@nl ;
    firebim:hasMember firebim:Member_3_5_2_2_1_1_2_1, firebim:Member_3_5_2_2_1_1_2_2 .

firebim:Member_3_5_2_2_1_1_2_1 a firebim:Member ;
    firebim:hasOriginalText "- hetzij, over de volledige hoogte, minstens een brandweerstand E 30 (io);"@nl .

firebim:Member_3_5_2_2_1_1_2_2 a firebim:Member ;
    firebim:hasOriginalText "- hetzij afwisselend om de twee bouwlagen minstens een brandweerstand EI 30 (io)."@nl .

firebim:Section_3_5_2_2_2 a firebim:Section ;
    firebim:hasID "3.5.2.2.2" ;
    firebim:hasOriginalText "Dubbelwandige gevel naar buiten toe open."@nl ;
    firebim:hasArticle firebim:Article_3_5_2_2_2_1 .

firebim:Article_3_5_2_2_2_1 a firebim:Article ;
