    firebim:hasOriginalText """     -    20 m van de evacuatieweg die de uitgangen verbindt;"""@nl .

firebim:Member_4_4_1_1_1_2_2 a firebim:Member ;
    firebim:hasOriginalText """     -    30 m van de toegang tot de dichtstbijzijnde uitgang;"""@nl .

